/* 自定义企业信息卡片 */
import React from 'react';
import { Image } from 'antd';
import './css/tooltip.less';

type labelValue = { value: string; label?: string; entId?: string };

interface PropTypes {
  data: {
    fallbackImg: string;
    pic: string;
    name: string;
    dataId?: string;
    nums?: labelValue[];
    infos?: labelValue[];
    tags?: [];
  };
  onTitleClick?: (data: labelValue) => void;
  onValueClick?: (data: labelValue) => void;
}

interface PropTypesLabel {
  data: labelValue;
  onValueClick?: (data: labelValue) => void;
  onTitleClick?: (data: labelValue) => void;
}

const tagsColor = [
  { bg: '#FEF1DF', color: '#FF8411' },
  { bg: '#DFECFF', color: '#6E8EFC' },
  { bg: '#DFECFF', color: '#4387EA' },
  { bg: '#E8F7E3', color: '#50B72A' },
  { bg: '#FEE9E9', color: '#F62828' },
];

const LabelValue: React.FC<PropTypesLabel> = (props) => {
  const { value, label, entId } = props.data;
  return (
    <div className="label-value-box">
      <span className="tooltip-label">{label || '-'}</span>
      <span
        className="tooltip-value"
        style={{
          color: entId ? '#4b74ff' : '#363b4d',
          cursor: entId ? 'pointer' : '',
        }}
        onClick={() => {
          props.onValueClick && props.onValueClick(props.data);
        }}
      >
        {value || '-'}
      </span>
    </div>
  );
};

const MyTooltip: React.FC<PropTypes> = (props) => {
  const { data } = props;
  const logo = data.pic
    ? data.pic?.slice(0, 4) === 'data'
      ? data.pic
      : `${data.pic}!qikewater`
    : '';

  return (
    <>
      {JSON.stringify(data) === '{}' ? (
        <div className="tooltip-nodata">
          <img src="data:image/png;base64," />
          暂无数据
        </div>
      ) : (
        <div className="my-tooltip">
          <div className="tooltip-top">
            <div className="top-left">
              <Image
                width={52}
                height={52}
                src={logo}
                preview={false}
                fallback={data.fallbackImg}
                className="img-logo"
              />
            </div>
            <div className="top-right">
              <div
                className="person-name"
                style={{
                  color: data.dataId ? '#4B74FF' : '#363b4d',
                  cursor: data.dataId ? 'pointer' : '',
                }}
                onClick={() => {
                  props.onTitleClick &&
                    props.onTitleClick({
                      value: data.name,
                      entId: data.dataId,
                    });
                }}
              >
                {data.name || '-'}
              </div>
              {data.tags ? (
                <div className="tags-box">
                  {data.tags.map((tag, index) => (
                    <div
                      style={{
                        color: tagsColor[index % 5].color,
                        background: tagsColor[index % 5].bg,
                      }}
                      className="tooltip-tag"
                      key={tag}
                    >
                      {tag}
                    </div>
                  ))}
                </div>
              ) : (
                ''
              )}
              <div>
                {data.infos &&
                  data.infos.map((info, index) => (
                    <LabelValue
                      key={`${info.value}-${index}`}
                      data={info}
                      onValueClick={props.onValueClick}
                    />
                  ))}
              </div>
            </div>
          </div>
          {data.nums ? (
            <div className="tooltip-bottom">
              {data.nums.map((num, index) => (
                <LabelValue
                  key={`${num.value}-${index}`}
                  data={num}
                  onValueClick={props.onValueClick}
                />
              ))}
            </div>
          ) : (
            ''
          )}
        </div>
      )}
    </>
  );
};

export default MyTooltip;
